// 3rd party
import { List, Map } from "immutable";

// project dependencies
import { PianoInstrument } from "./instruments/Piano";
import { WaveformVisualizer } from "./visualizers/Waveform";
import { TrumpetInstrument } from "./instruments/SurajBajgain";
import { CircleSquareVisualizer } from "./visualizers/SurajBajgain";
import { DrumInstrument } from './instruments/rupakkhatri';
import { SquaresVisualizer } from './visualizers/rupakkhatri';
import { FluteInstrument} from './instruments/dthapa770';
import { CircleVisualizer} from './visualizers/dthapa770';
import {GuitarInstrument} from './instruments/shailendra1000';
import {BarVisualizer} from './visualizers/shailendra1000';

/** ------------------------------------------------------------------------ **
 * The entire application state is stored in AppState.
 ** ------------------------------------------------------------------------ */

/**
 * Observation: pure map (compare and contrast with impure map)
 *
 * 'instrument': Instrument
 * 'visualizer': Visualizer
 */
export type AppState = Map<string, any>;

const instruments = List([
  PianoInstrument,
  TrumpetInstrument,
  DrumInstrument,
  FluteInstrument,
  GuitarInstrument,

]);
const visualizers = List([
  WaveformVisualizer,
  CircleSquareVisualizer,
  SquaresVisualizer,
  CircleVisualizer,
  BarVisualizer,

]);
export const defaultState: AppState = Map<string, any>({
  instruments,
  visualizers,
});
